import React from 'react';

const Navbar = () => {
  return (
    <nav style={{
      backgroundColor: 'rgba(255, 255, 255, 0.8)',
      backdropFilter: 'blur(8px)',
      padding: '1.2rem 0',
      position: 'sticky',
      top: 0,
      zIndex: 100,
      borderBottom: 'var(--card-border)'
    }}>
      <div className="container" style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '0.5rem',
          fontSize: '1.5rem',
          fontWeight: 800,
          color: 'var(--primary)',
          letterSpacing: '-0.025em'
        }}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" style={{ width: '32px', height: '32px' }}>
            <path d="M1.5 8.67v8.58a3 3 0 003 3h15a3 3 0 003-3V8.67l-8.928 5.493a3 3 0 01-3.144 0L1.5 8.67z" />
            <path d="M22.5 6.908V6.75a3 3 0 00-3-3h-15a3 3 0 00-3 3v.158l9.714 5.978a1.5 1.5 0 001.572 0L22.5 6.908z" />
          </svg>
          <span>BlinkInbox</span>
        </div>
        <div style={{
          display: 'flex',
          gap: '2rem'
        }}>
          <NavLink href="#">Home</NavLink>
          <NavLink href="#">Features</NavLink>
          <NavLink href="#">FAQ</NavLink>
          <NavLink href="#">Contact</NavLink>
        </div>
      </div>
    </nav>
  );
};

const NavLink = ({ href, children }) => {
  return (
    <a 
      href={href} 
      style={{
        color: 'var(--gray-600)',
        textDecoration: 'none',
        fontWeight: 500,
        transition: 'all 0.2s ease',
        position: 'relative',
        padding: '0.25rem 0'
      }}
      onMouseEnter={(e) => {
        e.target.style.color = 'var(--primary)';
        const after = document.createElement('style');
        after.innerHTML = `
          ${e.target.tagName.toLowerCase()}[href="${href}"]::after {
            content: '';
            position: absolute;
            width: 100%;
            height: 2px;
            bottom: 0;
            left: 0;
            background-color: var(--primary);
            transition: width 0.2s ease;
          }
        `;
        document.head.appendChild(after);
      }}
      onMouseLeave={(e) => {
        e.target.style.color = 'var(--gray-600)';
        const styles = document.querySelectorAll('style');
        styles.forEach(style => {
          if (style.innerHTML.includes(`[href="${href}"]::after`)) {
            document.head.removeChild(style);
          }
        });
      }}
    >
      {children}
    </a>
  );
};

export default Navbar;
